class Solution {
public:
typedef long long ll;
    long long countSubarrays(vector<int>& arr, int k) {
        ll ans=0;
        unordered_map<ll,ll>mp;
         ll n=arr.size();
         int i=0;
       while(n--){
            unordered_map<ll,ll>mp1;
            if(arr[i]==k)
            ans++;
            mp1[arr[i]]=1;
            for(auto it: mp){
                ll x=it.first&arr[i];
                mp1[x]+=it.second;
                if(x==k)
                ans+=it.second;
            }
            mp=mp1;
            i++;
        }
        return ans;
    }
};
