class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        res = defaultdict(int)
        t = nums[0]
        ans = 0
        for _ in range(t.bit_length() - 1, -1, -1):
            if t >> _ & 1 < k >> _ & 1:
                break
        else:
            res[t] += 1
            ans += t == k


        for i in nums[1:]:
            n_r = defaultdict(int)
            for v in res:
                t = v & i
                for _ in range(t.bit_length() - 1, -1, -1):
                    if t >> _ & 1 < k >> _ & 1:
                        break
                else:
                    ans += res[v] if t == k else 0
                    n_r[t] += res[v]
            t = i
            for _ in range(t.bit_length() - 1, -1, -1):
                if t >> _ & 1 < k >> _ & 1:
                    break
            else:
                ans += 1 if t == k else 0
                n_r[t] += 1

            res = n_r
            # print(res)
        return ans
