class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        vector<unordered_map<int, long long>> dp(size);
        for (int index = 0; index < size; ++index) {
            dp[index][nums[index]]++;
            if (index != 0) {
                for (auto it : dp[index - 1]) {
                    dp[index][it.first & nums[index]] += it.second;
                }
            }
        }
        long long result = 0;
        for (int index = 0; index < size; ++index) {
            result += dp[index][k];
        }
        return result;
    }
};
