class Solution {
    #define ll long long
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n=v.size();
        ll int ans=0;
        unordered_map<ll int,ll int> mp;
        for(int i=0;i<n;i++){
            unordered_map<ll int,ll int> mp1;
            mp1[v[i]]=1;
            for(auto&it:mp){
                ll int res=(it.first&v[i]);
                if(res==k) ans+=it.second;
                mp1[res]+=it.second;
            }
            if(v[i]==k) ans++;
            mp=mp1;
        }
        return ans;
    }
};
