class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long total = 0;
        unordered_map<long long , long long>prevResult;
        for(int i=0;i<n;i++){
            unordered_map<long long, long long> currResult;
            if(nums[i]==k){
                total++;
            }
            currResult[nums[i]]=1;
            for(auto &[value, frequency]:prevResult){
                long long newAndRes= value & nums[i];
                if(newAndRes==k){
                    total+= frequency;
                }
                currResult[newAndRes]+=frequency;
            }
            prevResult= currResult;
        }
        return total;
    }
};
