class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long,long long >prev;
        for(int i =0 ;i < n; i++){
            unordered_map<long long ,long long> current;
            if(nums[i] == k)cnt++;
            current[nums[i]]=1;
            for(auto &[value, times]:prev){
                long long newresult = value & nums[i];
                if(newresult == k)cnt += times;
                current[newresult]+=times;
            }
            prev = current;
        }
        return cnt;
    }
};
