class Solution {
    public long countSubarrays(int[] nums, int k) {
        HashMap<Long,Integer> hm=new HashMap<>();
        HashMap<Long,Integer> hm2=new HashMap<>();
        long ans=0;
        for(int i=0;i<nums.length;i++){
            if(i%2==0){
                hm2.clear();
                for(Map.Entry<Long,Integer> me:hm.entrySet()){
                                long add=(me.getKey()&nums[i]);
                                if(add==k){
                                    ans+=me.getValue();
                                }
                                 if(add>=k){
                                    hm2.put(add,hm2.getOrDefault(add,0)+me.getValue());
                                }
                            }
                            if(nums[i]>=k){
                                hm2.put((long)nums[i],hm2.getOrDefault((long)nums[i],0)+1);
                            }
                            if(nums[i]==k){
                                ans++;
                            }
            }else{
                hm.clear();
                for(Map.Entry<Long,Integer> me:hm2.entrySet()){
                                long add=(me.getKey()&nums[i]);
                                if(add==k){
                                    ans+=me.getValue();
                                }
                                 if(add>=k){
                                    hm.put(add,hm.getOrDefault(add,0)+me.getValue());
                                }
                            }
                            if(nums[i]>=k){
                                hm.put((long)nums[i],hm.getOrDefault((long)nums[i],0)+1);
                            }
                            if(nums[i]==k){
                                ans++;
                            }
            }
            // System.out.println(ans+" "+i);
            // System.out.println(hm);
            // System.out.println(hm2);

        }
        return ans;
    }
}
