class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> s;
        long long ans = 0;
        for (int i : nums) {
            unordered_map<int, int> t;
            for (auto& x : s) {
                t[x.first & i] += x.second;
            }
            t[i]++;
            ans += t[k];
            s = t;
        }
        return ans;
    }
};
