class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ct = 0;

        unordered_map<long long, long long> pv;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr; 

            if (nums[i] == k) {
                ++ct;
            }
            cr[nums[i]] = 1;

            for (auto& [v, f] : pv) {
                long long na = v & nums[i];
                if (na == k) {
                    ct += f;
                }
                cr[na] += f;
            }

            pv = cr;
        }

        return ct;
    }
};
