class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        long long ans = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < nums.size(); i++) {
            unordered_map<long long, long long> cur; 
            if (nums[i] == target) ans++;
            cur[nums[i]] = 1;
            for (auto& [v, freq] : prev) {
                long long newR = v & nums[i];
                if (newR == target) {
                    ans += freq;
                }
                cur[newR] += freq;
            }

            prev = cur;
        }
        return ans;
    }
};
