class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll n = a.size(), ans = 0;
        unordered_map<ll, ll> mp;
        fr(i,n){
            unordered_map<ll,ll> tm;
            if(a[i] == k) ans++;
            tm[a[i]] = 1;
            for (auto it: mp){
                ll t1 = it.first & a[i];
                if(t1 == k){
                    ans += it.second;
                }
                tm[t1] += it.second;
            }
            mp = tm;
        }
        return ans;
    }
};
