class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int size = nums.size();
        int pos[30], kPos[30];
        for(int i = 0; i < 30; i++)
        {
            if((k & (1 << i)) == 0) kPos[i] = 1;
            pos[i] = -1;
        }
        for(int i = 0; i < size; i++)
        {
            int minPos = i, maxPos = -1;
            for(int j = 0; j < 30; j++)
            {
                if((nums[i] & (1 << j)) == 0) pos[j] = i;
                if(kPos[j])
                {
                    minPos = min(minPos, pos[j]);
                }else{
                    maxPos = max(maxPos, pos[j]);
                }
            }
            if(maxPos < minPos) ans += minPos - maxPos;
        }
        return ans;
    }
};
