class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        res=0
        prev={}
        for i in range(n):
            curr=defaultdict(int)
            if(nums[i]==k): res+=1
            curr[nums[i]]=1
            for a,b in prev.items():
                newAND=a&nums[i]
                if(newAND==k): res+=b
                curr[newAND]+=b
            prev=curr
        return res
