class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        prev = {}

        for i in range(n):
            curr = {}

            if nums[i] == k:
                count += 1
            curr[nums[i]] = curr.get(nums[i], 0) + 1

            for val, freq in prev.items():
                new_and = val & nums[i]
                if new_and == k:
                    count += freq
                curr[new_and] = curr.get(new_and, 0) + freq

            prev = curr

        return count
