class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long  n = nums.size(),count=0;

        unordered_map<long long, long long> arr;

        for (int i = 0; i < n;i++) 
        {
            unordered_map<long long, long long> new_arr; 

            if (nums[i] == k) 
            {
                count++;
            }
            new_arr[nums[i]] = 1;

            for (auto& [store, mp] : arr) 
            {
                long long ans = store & nums[i];
                if (ans == k) 
                {
                    count += mp;
                }
                new_arr[ans] += mp;
            }

            arr = new_arr;
        }

    return count;
    }
};
