class Solution:
    def count_subarrays(self, nums, k):
        n = len(nums)
        count = 0
        prev = defaultdict(int)

        for i in range(n):
            curr = defaultdict(int)

            if nums[i] == k:
                count += 1
            curr[nums[i]] += 1

            for val, freq in prev.items():
                new_and = val & nums[i]
                if new_and == k:
                    count += freq
                curr[new_and] += freq

            prev = curr.copy()

        return count
    def countSubarrays(self, nums: List[int], k: int) -> int:
        return self.count_subarrays(nums, k)
