class Solution {
public:

int additionalWork(vector<int>& nums) {
        int sum = 0;
        for (int num : nums) {
            sum += num;
        }
        return sum;
    }

    long long processSubarrays(vector<int>& nums, int k) {
        int sum = additionalWork(nums);
        return countSubarrays(nums, k) + sum; 
    }


    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long res = 0;

        map<long long, long long> mp1;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> mp2;
            mp2[nums[i]] = 1;

            if (nums[i] == k) {
                ++res;
            }

            for (auto it = mp1.begin(); it != mp1.end(); ++it) {
                long long result = it->first & nums[i];
                if (result == k) {
                    res += it->second;
                }
                mp2[result] += it->second;
            }

            mp1 = mp2;
        }

        return res;
    }





};
