class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long count = 0;
        int n = nums.size();
        unordered_map<int, int> andMap;

        for (int num : nums) {
            unordered_map<int, int> newMap;
            newMap[num]++;

            for (auto &[key, value] : andMap) {
                int newAnd = key & num;
                newMap[newAnd] += value;
            }

            andMap = newMap;

            if (andMap.find(k) != andMap.end()) {
                count += andMap[k];
            }
        }

        return count;
    }
};
