class Solution {
public:
    long long sol(vector<int> &nums, int k) {

        long long ret = 0;
        vector<int> cnt(32);

        for(int l = 0, r = 0; r < nums.size(); r++) {
            int temp = nums[r], idx = 0;
            while(temp) {
                if(temp % 2)
                    cnt[idx]++;
                temp /= 2;
                idx++;
            }

            long long cur = 0;
            long long p = 1;
            for(int i = 0; i < 32; i++) {
                if(cnt[i] == r - l + 1)
                    cur += p;
                p *= 2;
            }

            while(cur < k && l <= r) {
                temp = nums[l], idx = 0;
                while(temp) {
                    if(temp % 2)
                        cnt[idx]--;
                    temp /= 2;
                    idx++;
                }

                l++;

                cur = 0, p = 1;
                for(int i = 0; i < 32; i++) {
                    if(cnt[i] == r - l + 1)
                        cur += p;
                    p *= 2;
                }
            }
            ret += r - l + 1;
        }
        return ret;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        return sol(nums, k) - sol(nums, k + 1);
    }
};
