class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        unordered_map<int, int> freq;

        for(int i=0;i<n;++i){
            unordered_map<int, int> new_freq;
            new_freq[nums[i]]=1;

            for(const auto& [val, count] : freq){
                int new_val = val & nums[i];
                new_freq[new_val] += count;
            }

            freq = new_freq;

            if (freq.find(k) != freq.end()) {
                ans += freq[k];
            }
        }

        return ans;
    }
};
