class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long cnt = 0;

        unordered_map<long long,long long> pre;

        for(int i=0;i<nums.size();i++){
         unordered_map<long long,long long> mpp;
            if(nums[i] == k) cnt++;

            mpp[nums[i]]++;

            for(auto it:pre){
                long long ran = it.first & nums[i];

                if(ran == k) cnt += it.second;

                mpp[ran] += it.second;

            }

            pre = mpp;
        }

        return cnt;
    }
};
