import java.util.HashMap;

public class Solution {
    public long countSubarrays(int[] sequence, int k) {
        int length = sequence.length;
        long subarrayCount = 0; 
        HashMap<Long, Long> previousMap = new HashMap<>();

        for (int index = 0; index < length; ++index) {
            HashMap<Long, Long> currentMap = new HashMap<>();
            if (sequence[index] == k) {
                ++subarrayCount;
            }
            currentMap.put((long) sequence[index], 1L);

            for (HashMap.Entry<Long, Long> entry : previousMap.entrySet()) {
                long value = entry.getKey();
                long frequency = entry.getValue();
                long result = value & sequence[index];

                if (result == k) {
                    subarrayCount += frequency;
                }
                currentMap.put(result, currentMap.getOrDefault(result, 0L) + frequency);
            }
            previousMap = currentMap;
        }
        return subarrayCount;
    }
}
