class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

         int n = nums.size();
        long long ginti = 0;

        unordered_map<long long, long long> purana;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> abhi; 

            if (nums[i] == k) {
                ++ginti;
            }
            abhi[nums[i]] = 1;

            for (auto& [v, occr] : purana) {
                long long nres = v & nums[i];
                if (nres == k) {
                    ginti += occr;
                }
                abhi[nres] += occr;
            }

            purana = abhi;
        }

        return ginti;

    }
};
