class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int,int> mp;
        long long cnt = 0;
        for(int i=0;i<n;i++){
            unordered_map<int,int> nmp;
            nmp[nums[i]] = 1;
            for(auto it : mp){
                int fir = it.first;
                int sec = it.second;
                int temp = fir & nums[i];
                nmp[temp] = nmp[temp]+sec;
            }
            mp = nmp;
            if(mp.find(k)==mp.end()) continue;
            cnt+=mp[k];
        }
        return cnt;
    }
};
