class Solution(object):
    def countSubarrays(self, nums, k):
        n = len(nums)
        mark1 = 0
        mark2 = {}

        for i in range(n):
            mark = {}

            if nums[i] == k:
                mark1 += 1
            mark[nums[i]] = 1

            for c1, g in mark2.items():
                ch = c1 & nums[i]
                if ch == k:
                    mark1 += g
                if ch in mark:
                    mark[ch] += g
                else:
                    mark[ch] = g
            mark2 = mark
        return mark1
