class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long cnt = 0;

        HashMap<Long, Long> oldResults = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> currResults = new HashMap<>(); 

            if (nums[i] == k) {
                ++cnt;
            }
            currResults.put((long) nums[i], 1L);

            for (HashMap.Entry<Long, Long> entry : oldResults.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();

                long res = val & nums[i];
                if (res == k) {
                    cnt += freq;
                }
                currResults.put(res, currResults.getOrDefault(res, 0L) + freq);
            }

            oldResults = currResults;
        }

        return cnt;
    }
}
