#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        map<ll, ll> currentAnds;

        for (int i = 0; i < n; ++i) {
            map<ll, ll> newAnds; 

            if (nums[i] == k) {
                ++count;
            }
            newAnds[nums[i]] = 1;

            for (auto it = currentAnds.begin(); it != currentAnds.end(); ++it) {
                ll val = it->first;
                ll freq = it->second;

                ll newAnd = val & nums[i];
                if (newAnd == k) {
                    count += freq;
                }
                newAnds[newAnd] += freq;
            }

            currentAnds = newAnds;
        }

        return count;
    }
};
