from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        c = 0
        prev = defaultdict(int)

        for i in range(n):
            curr = defaultdict(int)
            if nums[i] == k:
                c += 1
            curr[nums[i]] = 1

            for ele, f in prev.items():
                next_val = ele & nums[i]
                if next_val == k:
                    c += f
                curr[next_val] += f

            prev = curr

        return c
