#define ll long long
#define pll pair<ll , ll>

class Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {

        ll n = nums.size();
        map<ll , pll> info;
        ll ans = 0;


        for(ll i = n-1 ; i >= 0 ; i --)
        {
            map<ll , pll> newinfo;
            for(auto k : info)
            {
               if(!newinfo.count(k.first&nums[i])) 
               {
                    newinfo[k.first&nums[i]] = k.second; 
               }
               else
               {
                    newinfo[k.first&nums[i]].first  = min(newinfo[k.first&nums[i]].first  , k.second.first);
                    newinfo[k.first&nums[i]].second  = max(newinfo[k.first&nums[i]].second  , k.second.second);
               }
            }

               if(!newinfo.count(nums[i])) 
               {
                    newinfo[nums[i]] = {i,i}; 
               }
               else
               {
                    newinfo[nums[i]].first  = min(newinfo[nums[i]].first  , i);
                    newinfo[nums[i]].second  = max(newinfo[nums[i]].second  , i);
               }


            if(newinfo.count(k)) ans += newinfo[k].second - newinfo[k].first + 1;
            info = newinfo;
        }



        return ans;
    }
};
