class Solution {
public:
long long countSubarrays(vector<int>& a, int t) {
int s = a.size();
long long c = 0;
unordered_map<int, long long> pf;
for (int i = 0; i < s; ++i) {
unordered_map<int, long long> cf;
cf[a[i]] = 1;
for (auto& p : pf) {
int r = p.first & a[i];
cf[r] += p.second;
if (r == t) {
c += p.second;
}
}
if (a[i] == t) {
++c;
}
pf = cf;
}
return c;
}
};
