class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        z = 0
        d = {}

        for i in range(n):
            d1 = {}
            for key in d:
                pope = key & nums[i]
                if pope == k:
                    z += d[key]
                if pope not in d1:
                    d1[pope] = 0
                d1[pope] += d[key]

            if nums[i] == k:
                z += 1

            if nums[i] not in d1:
                d1[nums[i]] = 0
            d1[nums[i]] += 1

            d = d1

        return z
