import java.util.*;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        long count = 0;
        Map<Integer, Integer> map = new HashMap<>();

        for (int i=0;i<nums.length;i++) {
            Map<Integer, Integer> newMap = new HashMap<>();

            if (nums[i] == k)
                count++;
            newMap.put(nums[i], 1);

            for (int key : map.keySet()) {
                int newKey = key & nums[i];
                newMap.put(newKey, newMap.getOrDefault(newKey, 0) + map.get(key));
                if (newKey == k)
                    count += map.get(key);
            }

            map = newMap;
        }        
        return count;
    }
}
