class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long> mp;
        for (int i = 0; i<n; ++i) {
            unordered_map<long long, long long> currR; 
            if (nums[i] == k) {
                ++cnt;
            }
            currR[nums[i]] = 1;
            for (auto& [value, frequency] : mp) {
                long long newAndResult = value & nums[i];
                if (newAndResult == k) {
                    cnt += frequency;
                }
                currR[newAndResult] += frequency;
            }
            mp = currR;
        }
        return cnt;
    }

    };
