class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size=nums.size();
        map<long long int,long long int> prefix;
        long long int ans=0;

        for(int i=0;i<size;i++)
        {
            map<long long int,long long int> mpp;
            if(nums[i]==k) ans++;
            mpp[nums[i]]=1;
            for( const auto &[v,f]:prefix)
            {
                long long neww=v&nums[i];
                if(neww==k)
                {
                    ans=ans+f;
                }
                mpp[neww]+=f;

            }
            prefix=mpp;
}
        return ans;

    }
};
