class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
    int n = nums.size();
    long long count = 0;
    unordered_map<int, int> andMap;

    for (int i = 0; i < n; ++i) {
        unordered_map<int, int> newMap;
        for (auto &[andResult, freq] : andMap) {
            int newAndResult = andResult & nums[i];
            if (newAndResult == k) {
                count += freq;
            }
            newMap[newAndResult] += freq;
        }
        newMap[nums[i]] += 1;
        if (nums[i] == k) {
            ++count;
        }
        andMap = newMap;
    }

    return count; 
    }
};
