class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        map<long long, long long> prev;

        for (int i = 0; i < n; ++i) {
            map<long long, long long> curr; 

            if (nums[i] == k) {
                cnt++;
            }
            curr[nums[i]] = 1;

            for (auto it = prev.begin(); it != prev.end(); ++it) {
                long long val = it->first;
                long long freq = it->second;

                long long temp = val & nums[i];
                if (temp == k) {
                    cnt += freq;
                }
                curr[temp] += freq;
            }

            prev = curr;
        }

        return cnt;
    }
};
