class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        vector<vector<int>> v(32);
        for(int i = 0 ; i<n ; i++){
            for(int j = 0 ; j<32 ; j++){
                if(!(nums[i]&(1<<j))){
                    v[j].push_back(i);
                }
            }
        }

        for(int i = 0 ; i<n ; i++){
            bool check = true;
            int s=i,e=n;
            for(int j = 0 ; j<32 ; j++){
                if((!(nums[i]&(1<<j))) && (k&(1<<j))){
                    check = false;
                    break;

                }else if((nums[i]&(1<<j)) && (!(k&(1<<j)))){
                    auto it = upper_bound(v[j].begin(),v[j].end(),i);
                    if(it==v[j].end()){
                        check = false;
                        break;
                    }else{
                        s=max(s,*it);
                    }
                }else if((nums[i]&(1<<j)) && (k&(1<<j))){
                    auto it = upper_bound(v[j].begin(),v[j].end(),i);
                    if(it!=v[j].end()){
                        e=min(e,*it);
                    }
                }
            }
            if(check){
                if(e>=s){
                    ans+=e-s;
                }
            }
        }

        return ans;
    }
};
