class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res=0;
        unordered_map<int,int>count;
        for (int num : nums) {
            unordered_map<int,int> newCount;
            newCount[num]++;
            for (auto [val,freq] : count) {
                newCount[val & num]+=freq;
            }
            count=newCount;
            res+=count[k];
        }
        return res;
    }
};
