/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var countSubarrays = function(nums, k) {
    const n = nums.length;
    let count = 0;

    // Map to store previous results
    let prevResults = new Map();

    for (let i = 0; i < n; ++i) {
        // Map to store current results
        let currResults = new Map();

        if (nums[i] === k) {
            ++count;
        }
        currResults.set(nums[i], 1);

        for (let [val, freq] of prevResults) {
            let newAndResult = val & nums[i];
            if (newAndResult === k) {
                count += freq;
            }
            currResults.set(newAndResult, (currResults.get(newAndResult) || 0) + freq);
        }

        prevResults = currResults;
    }

    return count;
};

// Example usage:
console.log(countSubarrays([1, 2, 3, 4], 1)); // Output depends on the input array and k value
console.log(countSubarrays([5, 1, 2, 3], 0)); // Output depends on the input array and k value
