class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        freq = defaultdict(int)

        for num in nums:
            new_freq = defaultdict(int)

            if num == k:
                count += 1

            for key in freq:
                new_and = key & num
                if new_and == k:
                    count += freq[key]
                new_freq[new_and] += freq[key]

            new_freq[num] += 1
            freq = new_freq

        return count
