class Solution {
    public long countSubarrays(int[] arr, int G) {
        int arr_len = arr.length;
        long chaitanya_ans = 0;
        HashMap<Long, Long> pr_mpf = new HashMap<>();

        for (int id = 0; id < arr_len; ++id) {
            HashMap<Long, Long> cr_mpf = new HashMap<>();

            if (arr[id] == G) {
                ++chaitanya_ans;
            }
            cr_mpf.put((long) arr[id], 1L);

            for (HashMap.Entry<Long, Long> et : pr_mpf.entrySet()) {
                long val = et.getKey();
                long frs = et.getValue();
                long n_A_r = val & arr[id];
                if (n_A_r == G) {
                   chaitanya_ans += frs;
                }
                cr_mpf.put(n_A_r, cr_mpf.getOrDefault(n_A_r, 0L) + frs);
            }

            pr_mpf = cr_mpf;
        }
        return chaitanya_ans;
    }
}
