class Solution {
public:
    long long countSubarrays(vector<int>& nums, int targetValue) {
        int numsSize = nums.size();
        long long totalCount = 0;
        unordered_map<long long, long long> prevResults;

        for (int idx = 0; idx < numsSize; ++idx) {
            unordered_map<long long, long long> currResults;

            if (nums[idx] == targetValue) {
                ++totalCount;
            }

            currResults[nums[idx]] = 1;

            for (auto& [val, freq] : prevResults) {
                long long newAndResult = val & nums[idx];
                if (newAndResult == targetValue) {
                    totalCount += freq;
                }
                currResults[newAndResult] += freq;
            }

            prevResults = currResults;
        }

        return totalCount;
    }
};
