class Solution {
public:
    long long solve(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> p;

        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> c;
            if (nums[i] == k) {
                ans++;
            }
            c[nums[i]] = 1;
            for(auto& a : p) {
                long long res = (a.first) & nums[i];
                if(res == k) {
                    ans += a.second;
                }
                c[res] += a.second;
            }
            p = c;
        }

        return ans;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums, k);
    }
};
