class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        #define ll long long
        int n=a.size();
        int m=log2(n);
        vector<vector<int>> f(n,vector<int>(m+1));
        for(int i=0;i<n;++i)
            f[i][0]=a[i];
        for(int j=1,len=2;j<=m;++j,len<<=1)
            for(int i=0;i+len-1<n;++i)
                f[i][j]=f[i][j-1]&f[i+len/2][j-1];
        auto query=[&](int l,int r){
            r=min(r,n-1);
            int k=log2(r-l+1),len=1<<k;
            return f[l][k]&f[r-len+1][k];
        };
        ll ans=0;
        for(int i=0;i<n;++i){
            int l=i;
            int r=n-1;
            while(l<=r){
                int mid=l+r>>1;
                if(query(i,mid)<=k)
                    r=mid-1;
                else
                    l=mid+1;
            }
            if(query(i,l)!=k)
                continue;
            int x=l;
            l=i;
            r=n-1;
            while(l<=r){
                int mid=l+r>>1;
                if(query(i,mid)<k)
                    r=mid-1;
                else
                    l=mid+1;
            }
            ans+=l-x;
        }
        return ans;
    }
};
