class Solution {
    public long countSubarrays(int[] nums, int k) {

        int n=nums.length;
        long ct=0;
        HashMap<Long,Long> big=new HashMap<>();
        for(int i=0;i<n;i++){
            HashMap<Long,Long> small=new HashMap<>();
           small.put((long)nums[i],(long)1);

            for(HashMap.Entry<Long,Long> key: big.entrySet()){

                long val=key.getKey();

                long com=key.getValue();

                if((val&nums[i]) == k) ct=ct+com;
                small.put(val&nums[i],small.getOrDefault(val&nums[i],(long)0)+com);


            }
            if(nums[i]==k) ct++;
            big=small;


        }
        return ct;
    }
}
