class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long, long long>prev,curr;
        for (int i = 0; i < n; ++i) {
            curr.clear();
            if (nums[i] == k) {
                cnt++;
            }
            curr[nums[i]] += 1;
            for (auto& [val, sa] : prev) {
                long long newr = (nums[i]&val);
                if (newr == k) {
                    cnt += sa;
                }
                curr[newr]+=sa;
            }
            prev.swap(curr);
        }
        return cnt;
    }
};
