class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
            long total = 0;
            HashMap<Long, Long> storeSeen = new HashMap<>(); // isko use karo store krne k liye

            for (int j : nums) {
                HashMap<Long, Long> storeCurr = new HashMap<>();
                if (j == k) ++total;
                long num = (long) j;
                storeCurr.put(num, (long)1); // long me save kro

                // Iterite karo
                for (Map.Entry<Long, Long> entry : storeSeen.entrySet()) {
                    long val = entry.getKey(), fq = entry.getValue();
                    long andres = val & j;
                    if (andres == k) total += fq;
                    // update karo
                    storeCurr.put(andres, storeCurr.getOrDefault(andres, 0L) + fq);
                }
                storeSeen = storeCurr;
            }

            return total; 
    }
}
