class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> andResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentResults; 

            if (nums[i] == k) {
                count++;
            }
            currentResults[nums[i]] = 1;

            for (auto& [prevVal, freq] : andResults) {
                long long newAndResult = prevVal & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                currentResults[newAndResult] += freq;
            }

            andResults = currentResults;
        }

        return count;
    }
};
