class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        count = {-1: 1}  # 以当前位结尾的子数组的按位与值的计数器；按位与的默认值为-1
        for a in nums:
            new = {-1: 1}
            for b, v in count.items():
                if b & a == k:
                    ans += v
                new[b & a] = new.get(b & a, 0) + v
            count = new
        return ans
