class Solution:
    def countSubarrays(self, nums, k):
        n = len(nums)
        count = 0

        prevResults = {}

        for i in range(n):
            currResults = {}

            if nums[i] == k:
                count += 1
            currResults[nums[i]] = 1

            for val, freq in prevResults.items():
                newAndResult = val & nums[i]
                if newAndResult == k:
                    count += freq
                if newAndResult in currResults:
                    currResults[newAndResult] += freq
                else:
                    currResults[newAndResult] = freq

            prevResults = currResults

        return count
